CREATE TABLE IF NOT EXISTS user_account (id SERIAL, login VARCHAR(255) NOT NULL, email VARCHAR(255) NOT NULL, password VARCHAR(255) NOT NULL, is_admin BOOLEAN NOT NULL, PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS trail (id SERIAL, author_id INTEGER NOT NULL, name VARCHAR(255) NOT NULL, description TEXT NOT NULL, country VARCHAR(255) NOT NULL, mountain VARCHAR(255) NOT NULL, beauty_rank DECIMAL(5, 2), technical_rank DECIMAL(5, 2), endurance_rank DECIMAL(5, 2), gpx_file_link VARCHAR(255), PRIMARY KEY (id), FOREIGN KEY (author_id) REFERENCES user_account (id));
CREATE TABLE IF NOT EXISTS comment (id SERIAL, author_id INTEGER NOT NULL, trail_id INTEGER NOT NULL, comment TEXT NOT NULL, PRIMARY KEY (id), FOREIGN KEY (author_id) REFERENCES user_account (id), FOREIGN KEY (trail_id) REFERENCES trail (id));
